function [U,U1,U2,W,W1,W2,y] = TestFSC_jf(m,Theta,n,yinf,H,ifile,iplot)% [U,U1,U2,W,W1,W2,y] = TestFSC(m,Theta,n,yinf,H,ifile,iplot)%   Driver to test Falkner-Skan-Cooke base flow%   allocate vectors & matrices    if ( nargin <= 6 )  iplot = 0 ; end    if ( nargin == 5 )  ifile = 0 ; end    xi = zeros(n+1, 1);    y = xi; U = xi;  U1 = xi; U2 = xi; W = xi;  W1 = xi; W2 = xi;    T0 = zeros(n+1,n+1);      T1 = T0;  T2 = T0;  T3 = T0;  T4 = T0;  %   collocate, make Chebysev polynomials and derivatives    xi = ChbGssLbt(n)';    T0 = Tpoly(n,xi);    T1 = dTpoly(T0);  T2 = dTpoly(T1);      T3 = dTpoly(T2);  T4 = dTpoly(T3);%   Project into physical space    [y, T1, T2, T3, T4] = AlgStr(yinf, H, xi, T1, T2, T3, T4);%   Solve base flow    [U, U1, U2, W, W1, W2] = FSC(m, Theta, y, T0, T1, T2, T3) ;%   Plot%     if iplot%       figure; % figure(1), clf%       plot(U, y, 'k', U1, y, 'r', U2, y, 'b', W, y, 'k', W1, y, 'r', ...%         W2, y, 'b')%       legend('U', 'dU', 'd^2U', 'W', 'dW', 'd^2W')%       xlabel('U, W and derivs'); ylabel('y')%       zoom on;            %     end%   Dump to file    if ifile      ZZ = [ y U U1 U2 W W1 W2 ]' ;      fd = fopen('fscbase.dat','w') ;      fprintf(fd,'%30.20e %30.20e %30.20e %30.20e %30.20e %30.20e %30.20e\n',ZZ) ;      fclose(fd) ;  end  